mapper.select:
SELECT DATE_FORMAT(create_date, '%Y-%m-%d') AS dateTimes, COUNT(*) AS addUser FROM tb_threenets_child_order WHERE DATE_FORMAT(create_date, '%Y-%m') = #{start} AND is_monthly = 2
AND operator = #{operate}
GROUP BY DATE_FORMAT(create_date, '%Y-%m-%d')

SELECT DATE_FORMAT(create_date, '%Y-%m-%d') AS dateTimes, COUNT(*) AS unsubscribeUser FROM tb_threenets_child_order WHERE DATE_FORMAT(create_date, '%Y-%m') = #{start} AND is_monthly = 3
AND operator = #{operate}
GROUP BY DATE_FORMAT(create_date, '%Y-%m-%d')

